package hashtable;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.HashSet;


public class App {


    public static void main(String[] args) {

    }
    public  ArrayList<ArrayList<String>> hashmap_left_join(java.util.HashMap hashMap1, HashMap hashMap2){
       ArrayList<ArrayList<String>> result= new ArrayList();
       ArrayList<String> any=new ArrayList<>();
        for (Object key:
             hashMap1.keySet()) {
            if(hashMap2.containsKey(key)){

                any.add((String) key);
                any.add(hashMap1.get(key).toString());
                any.add(hashMap2.get(key).toString());


            }else {


                any.add((String) key);
                any.add(hashMap1.get(key).toString());
                any.add(null);




            }

            result.add(any);
            any=new ArrayList<>();

        }


        return result;
    }
    public static String Most_common_word(String word){
        String[] parts = word.split(" ");
        HashSet<String> temp = new HashSet<>();
        int n = parts.length;
        int count = 0;

        for(int i = 0; i < n-1; i++) {
            String currentWord = parts[i];
            for(int j = i+1; j<n; j++) {
                String nextWord = parts[j];
                if(currentWord.contains(nextWord)) {
                    temp.add(nextWord);
                    count++;
                }
                if(nextWord.contains(currentWord)) {
                    temp.add(currentWord);
                    count++;
                }

            }

        }
        System.out.println(count);
return parts[count];
        
    }






}
